body.script:
$(function () {
    if (!$('.stats-tabs li.active').length) {
        if (history.replaceState) {
            history.replaceState({}, '', '?view=' + $('.stats-tabs li:first').addClass('active').children('a').data('view'));
        } else {
            $('.stats-tabs li:first').addClass('active');
        }
    }
})
